					where plan_id=_plan_id;
  Select 'SE HA ELIMINADO CORRECTAMENTE EL PLAN' as MENSAJE;

           elseif(_Opcion=3)
        then         
                    
       

catalogo_planes_lineas
(
plan_codigo,
plan_nombre,
plan_tipo,
plan_descripcion,
plan_costo,
plan_precio,
plan_empresa,
