values
(
_plan_codigo,
_plan_nombre,
_plan_tipo,
_plan_descripcion,
_plan_costo,
_plan_precio,
_plan_empresa,
_plan_proveedor
);
 Select 'SE HA INSERTADO CORRECTAMENTE EL PLAN' as MENSAJE;

	end if;


when  'wbs'
